import React, { useState, useEffect, useRef } from "react";
import { API } from "aws-amplify";
import { Table, InputGroup, Button, FormControl } from "react-bootstrap";
import _ from "lodash";

import { onError } from "../libs/errorLib";
import "./Profile.css";
import LoaderButton from "../components/LoaderButton";


export default function Profile() {

  const addDateRef = useRef(null)

  const [isAddLoading, setisAddLoading] = useState



  const [data, setData] = useState([{
    name: "Tesla",
    symbol: "TSLA",
    bid: "The Moon",
  },
  {
    name: "Apple",
    symbol: "AAPL",
    bid: 123,
  },
  {
    name: "Microsoft",
    symbol: "MSFT",
    bid: 235,
  },
  {
    name: "PayPal",
    symbol: "PYPL",
    bid: 189,
  },])

  const getData = () => {
    API.get("stonks", `/market?symbols=${addDataRef ? addDataRef.current.value : ""}`)
    .then(data => {
      console.log(data);
      setData(data.quoteResponse.result);
    })
    .catch(err => onError(err))
    .finally(() => setIsAddLoading(false));
  }

  return (
    <div className="Profile">
      <div className="">
        <h1>Stonks</h1>
        <p className="">Welcome to your profile!</p>
      </div>
      <InputGroup>
        <FormControl
          ref={addDataRef}
          placeholder="Symbols in format AAPL,IBM,TSLA"
          aria-describedby="basic-addon2"
        />
        <InputGroup.Append>
          <LoaderButton isLoading={isAddLoading} onClick={() => {
            setIsAddLoading(true);
            getData();
          }}>
            Get Info!
          </LoaderButton>
        </InputGroup.Append>
      </InputGroup>
      <Table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Symbol</th>
            <th>Bid</th>
          </tr>
        </thead>
        <tbody>
        {data.map(item => {return<tr key={item.symbol}> 
        <td>{item.symbol}</td>
        <td>{item.name}</td>
        <td>{item.bid}</td>
        </tr>})}
        </tbody>
      </Table>
    </div>
  );
}
